  width: 100%;
  padding: 10px;
  border: 1px solid #d1d1d1;
  border-radius: 5px;
  margin-top: 10px;
  font-weight: bold;
  &.warn { 
    border: 2px solid #ff7a7a;
  }
`;

const Button = styled.button`
  box-shadow: 0px 0.3em 0px 0px #2fb179;
  border-radius: 5px;
  background-color: #38cc8c;
  color: white;
  width: 100%;
  padding: 10px;
  border: none;
  cursor: pointer;
  margin: 15px 0;
`;

